using NesCs.Common.Tests;

namespace NesCs.UnitTests.Cpu;

public class BitTestOperationsInCpuMust
{
    [Theory]
    [MemberData(nameof(Opcode24JsonFeeder))]
    [MemberData(nameof(Opcode26JsonFeeder))]
    [MemberData(nameof(Opcode2AJsonFeeder))]
    [MemberData(nameof(Opcode2CJsonFeeder))]
    [MemberData(nameof(Opcode36JsonFeeder))]
    [MemberData(nameof(Opcode4AJsonFeeder))]
    [MemberData(nameof(Opcode6AJsonFeeder))]
    public void BeExecutedCorrectly(SampleCpu sampleCpu)
    {
        var trace = new List<(int, byte, string)>();
        var sut = Utilities.CreateSubjectUnderTestFromSample(sampleCpu.Opcodes, sampleCpu.Initial, trace);
        sut.Run();

        Utilities.Equal(sampleCpu.Final, sut);
        Utilities.Equal(sampleCpu.Cycles, trace);
    }

